USE [master]
;

CREATE DATABASE [bd_empresa] 
;

USE [bd_empresa]
;

CREATE TABLE [dbo].[alocacao] (
	[num_matricula] [int] NOT NULL ,
	[cod_projeto] [int] NOT NULL ,
	[num_horas] [int] NOT NULL 
) ON [PRIMARY]
;

CREATE TABLE [dbo].[departamento] (
	[cod_depto] [int] NOT NULL ,
	[nom_depto] [varchar] (100) COLLATE Latin1_General_CI_AS NOT NULL ,
	[num_matricula_gerente] [int] NULL ,
	[dat_inicio_gerente] [smalldatetime] NULL 
) ON [PRIMARY]
;

CREATE TABLE [dbo].[departamento_local] (
	[cod_depto] [int] NOT NULL ,
	[nom_local] [varchar] (50) COLLATE Latin1_General_CI_AS NOT NULL 
) ON [PRIMARY]
;

CREATE TABLE [dbo].[dependente] (
	[num_matricula] [int] NOT NULL ,
	[nom_dependente] [varchar] (100) COLLATE Latin1_General_CI_AS NOT NULL ,
	[sex_dependente] [char] (1) COLLATE Latin1_General_CI_AS NOT NULL ,
	[dsc_parentesco] [char] (10) COLLATE Latin1_General_CI_AS NOT NULL 
) ON [PRIMARY]
;

CREATE TABLE [dbo].[empregado] (
	[num_matricula] [int] NOT NULL ,
	[nom_empregado] [varchar] (100) COLLATE Latin1_General_CI_AS NOT NULL ,
	[dat_nascimento] [smalldatetime] NULL ,
	[dsc_endereco] [varchar] (100) COLLATE Latin1_General_CI_AS NULL ,
	[nom_cidade] [varchar] (100) COLLATE Latin1_General_CI_AS NULL ,
	[sig_uf] [char] (2) COLLATE Latin1_General_CI_AS NULL ,
	[sex_empregado] [char] (1) COLLATE Latin1_General_CI_AS NOT NULL ,
	[val_salario] [numeric](7, 2) NOT NULL ,
	[num_matricula_supervisor] [int] NULL ,
	[cod_depto] [int] NULL 
) ON [PRIMARY]
;

CREATE TABLE [dbo].[projeto] (
	[cod_projeto] [int] NOT NULL ,
	[nom_projeto] [varchar] (100) COLLATE Latin1_General_CI_AS NOT NULL ,
	[nom_local] [varchar] (50) COLLATE Latin1_General_CI_AS NULL ,
	[cod_depto] [int] NOT NULL 
) ON [PRIMARY]
;

ALTER TABLE [dbo].[alocacao] WITH NOCHECK ADD 
	CONSTRAINT [PK_alocacao] PRIMARY KEY  CLUSTERED 
	(
		[num_matricula],
		[cod_projeto]
	)  ON [PRIMARY] 
;

ALTER TABLE [dbo].[departamento] WITH NOCHECK ADD 
	CONSTRAINT [PK_departamento] PRIMARY KEY  CLUSTERED 
	(
		[cod_depto]
	)  ON [PRIMARY] 
;

ALTER TABLE [dbo].[departamento_local] WITH NOCHECK ADD 
	CONSTRAINT [PK_departamento_local] PRIMARY KEY  CLUSTERED 
	(
		[cod_depto],
		[nom_local]
	)  ON [PRIMARY] 
;

ALTER TABLE [dbo].[dependente] WITH NOCHECK ADD 
	CONSTRAINT [PK_dependente] PRIMARY KEY  CLUSTERED 
	(
		[num_matricula],
		[nom_dependente]
	)  ON [PRIMARY] 
;

ALTER TABLE [dbo].[empregado] WITH NOCHECK ADD 
	CONSTRAINT [PK_empregado] PRIMARY KEY  CLUSTERED 
	(
		[num_matricula]
	)  ON [PRIMARY] 
;

ALTER TABLE [dbo].[projeto] WITH NOCHECK ADD 
	CONSTRAINT [PK_projeto] PRIMARY KEY  CLUSTERED 
	(
		[cod_projeto]
	)  ON [PRIMARY] 
;

ALTER TABLE [dbo].[dependente] ADD 
	CONSTRAINT [CK_dependente_sexo] CHECK ([sex_dependente] = 'M' or [sex_dependente] = 'F')
;

ALTER TABLE [dbo].[empregado] ADD 
	CONSTRAINT [CK_empregado_sexo] CHECK ([sex_empregado] = 'M' or [sex_empregado] = 'F')
;


ALTER TABLE [dbo].[alocacao] ADD 
	CONSTRAINT [FK_alocacao_empregado] FOREIGN KEY 
	(
		[num_matricula]
	) REFERENCES [dbo].[empregado] (
		[num_matricula]
	),
	CONSTRAINT [FK_alocacao_projeto] FOREIGN KEY 
	(
		[cod_projeto]
	) REFERENCES [dbo].[projeto] (
		[cod_projeto]
	) ON DELETE CASCADE  ON UPDATE CASCADE 
;

ALTER TABLE [dbo].[departamento] ADD 
	CONSTRAINT [FK_gerencia] FOREIGN KEY 
	(
		[num_matricula_gerente]
	) REFERENCES [dbo].[empregado] (
		[num_matricula]
	)
;

ALTER TABLE [dbo].[departamento_local] ADD 
	CONSTRAINT [FK_esta_localizado] FOREIGN KEY 
	(
		[cod_depto]
	) REFERENCES [dbo].[departamento] (
		[cod_depto]
	) ON DELETE CASCADE  ON UPDATE CASCADE 
;

ALTER TABLE [dbo].[dependente] ADD 
	CONSTRAINT [FK_possui] FOREIGN KEY 
	(
		[num_matricula]
	) REFERENCES [dbo].[empregado] (
		[num_matricula]
	) ON DELETE CASCADE  ON UPDATE CASCADE 
;

ALTER TABLE [dbo].[empregado] ADD 
	CONSTRAINT [FK_supervisiona] FOREIGN KEY 
	(
		[num_matricula_supervisor]
	) REFERENCES [dbo].[empregado] (
		[num_matricula]
	),
	CONSTRAINT [FK_trabalha_para] FOREIGN KEY 
	(
		[cod_depto]
	) REFERENCES [dbo].[departamento] (
		[cod_depto]
	)
;


ALTER TABLE [dbo].[projeto] ADD 
	CONSTRAINT [FK_projeto_departamento] FOREIGN KEY 
	(
		[cod_depto]
	) REFERENCES [dbo].[departamento] (
		[cod_depto]
	)
;

